import classNames from 'classnames/bind';

import styles from './Header.module.scss';
import Menu from '~/components/Menu';
import Button from '~/components/Button';
import { useState } from 'react';

const cx = classNames.bind(styles);

const LIST_MENU_ITEMS = [
    {
        title: 'Đặt mục tiêu',
        page: '/',
    },
    {
        title: 'Theo dõi chi tiêu',
        page: '/follow',
    },
];

function Header (props)  {
  
    const [user, setUser] = useState(false);
    function sendData () {
       props.parentC(user);
    }
    const handleSetUser = () => {
        
        setUser((prev) => !prev);
        sendData();
    };
    
    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('shopName')}>Trang chủ</div>

                <Menu items={LIST_MENU_ITEMS} />

                {/* user || login */}
                {user ? (
                    <Button outline rounded onClick={handleSetUser}>
                        Hidden
                    </Button>
                ) : (
                    <Button outline rounded onClick={handleSetUser}>
                        Sign in
                    </Button>
                )}
                
            </div>
            <div><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-box-arrow-right" viewBox="0 0 16 16">
  <path fill-rule="evenodd" d="M10 12.5a.5.5 0 0 1-.5.5h-8a.5.5 0 0 1-.5-.5v-9a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v2a.5.5 0 0 0 1 0v-2A1.5 1.5 0 0 0 9.5 2h-8A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h8a1.5 1.5 0 0 0 1.5-1.5v-2a.5.5 0 0 0-1 0v2z"/>
  <path fill-rule="evenodd" d="M15.854 8.354a.5.5 0 0 0 0-.708l-3-3a.5.5 0 0 0-.708.708L14.293 7.5H5.5a.5.5 0 0 0 0 1h8.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3z"/>
</svg></div>
        </header>
    );
};

export default Header;
