import classNames from 'classnames/bind';

import styles from './DefaultLayout.module.scss';
import Input from '~/components/Input';
import Header from '~/components/Layouts/components/Header';
import Section from '~/components/Section';
import { useState } from 'react';
import Login from '~/components/SignUpLogin/Login';

const cx = classNames.bind(styles);


function DefaultLayout({ children }) {

    function login(value) {
        if (value == "DK")
            setlogin(false)
        else
            setlogin(true)
        console.log(value)
    }
    const [user, setUser] = useState('false');
    const [logins, setlogin] = useState('false');
   
    function getUser(value) {
        console.log(user)
        setUser(value)
    }
    return (

        <div className={cx('wrapper ')}>
            <Header parentC={getUser} />


            <div className='row mt-3'>
                <div className={cx('container col-4')}>
                    <div className={cx('inner')}>
                        <Section center >
                            <form className={cx('form')}>
                                <div>
                                    <Input label="Thu nhập tháng" type="number" id="form__inp-income" min="0" underline />
                                </div>

                                <div className=''>
                                    <Input label="Thời gian thực hiện từ" type="date" id="form__inp-from" underline />
                                    <Input label="đến" type="date" id="form__inp-to" underline />
                                </div>
                            </form>
                        </Section>

                        <Section center >{children}</Section>
                    </div>

                </div>
                <div className={user ? 'col-3' : 'collapsing'}>

                    <Login getLogin={login}></Login>
                    <div className={logins ? "collapsing" : "card shadow border-2"}>
                        <div className='card-header'>
                            <div className='title'><b>Đăng ký</b></div>
                        </div>
                        <div className='card-body'>
                            <form>
                                <input type='email' className='form-control mb-2' placeholder='Email hoặc số điện thoại '></input>
                                <input type='password' className='form-control mb-2' placeholder='Mật khẩu '></input>
                                <input type='password' className='form-control mb-2' placeholder='Nhập lại mật khẩu '></input>
                                <div className='card-footer text-center' >
                                    <button className='btn btn-success mx-1 w-40' type='Button'  >Đăng ký</button>
                                    {/* Sử lý lệnh đăng ký(kiểm tra mật khẩu và đưa vào database) */}
                                    <button className='btn btn-primary ms-1 w-40' type='Button' name='DN' onClick={(e) => login(e.target.name)}>Đăng nhập</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
                <div>
                </div>

            </div>
        </div>
    );
};

export default DefaultLayout;
